[...document.querySelectorAll(".control")].forEach((e=>{e.addEventListener("click",(function(){document.querySelector(".active-btn").classList.remove("active-btn"),this.classList.add("active-btn"),document.querySelector(".active").classList.remove("active"),document.getElementById(e.dataset.id).classList.add("active")}))})),document.querySelector(".theme-btn").addEventListener("click",(()=>{document.body.classList.toggle("light-mode");const e=document.querySelector('meta[name="color-scheme"]');"light"===e.content?e.content="dark":e.content="light"}));