var userScore=0,computerScore=0;const userScore_span=document.getElementById("user-score"),computerScore_span=document.getElementById("computer-score"),result_div=document.querySelector(".result"),rock_div=document.getElementById("rock"),paper_div=document.getElementById("paper"),scissors_div=document.getElementById("scissors"),restart_div=document.querySelector(".reset");function getComputerChoice(){return["rock","paper","scissors"][Math.floor(3*Math.random())]}function convertCase(e){return"paper"===e?"Paper":"scissors"===e?"Scissors":"Rock"}function win(e,s){userScore++,userScore_span.innerHTML=userScore;const r=" (user)".fontsize(3).sup(),t=" (comp)".fontsize(3).sup();result_div.innerHTML=`<p>${convertCase(e)}${r} beats ${convertCase(s)}${t}. You win!</p>`;const o=document.getElementById(e);o.classList.add("winningStyles"),setTimeout((()=>o.classList.remove("winningStyles")),300)}function loses(e,s){computerScore++,computerScore_span.innerHTML=computerScore;const r=" (user)".fontsize(3).sup(),t=" (comp)".fontsize(3).sup();result_div.innerHTML=`<p>${convertCase(s)}${t}\n    beats ${convertCase(e)}${r}. You lose!</p>`;const o=document.getElementById(e);o.classList.add("losingStyles"),setTimeout((()=>o.classList.remove("losingStyles")),300)}function draw(e,s){" (user)".fontsize(3).sup()," (comp)".fontsize(3).sup();result_div.innerHTML=`<p>It was a draw! You both chose ${convertCase(e)}</p>`;const r=document.getElementById(e);r.classList.add("drawStyles"),setTimeout((()=>r.classList.remove("drawStyles")),300)}function game(e){const s=getComputerChoice();switch(e+s){case"paperrock":case"rockscissors":case"scissorspaper":win(e,s);break;case"rockpaper":case"scissorsrock":case"paperscissors":loses(e,s);break;case"rockrock":case"scissorsscissors":case"paperpaper":draw(e,s);break}}function handleRestartGame(){userScore=0,computerScore=0,userScore_span.innerHTML=userScore,computerScore_span.innerHTML=computerScore,result_div.innerHTML="Start again..."}function main(){rock_div.addEventListener("click",(()=>game("rock"))),paper_div.addEventListener("click",(()=>game("paper"))),scissors_div.addEventListener("click",(()=>game("scissors"))),restart_div.addEventListener("click",handleRestartGame)}main();