let userScore=0,computerScore=0;const userScore_span=document.getElementById("user-score"),computerScore_span=document.getElementById("computer-score"),scoreBoard_div=document.querySelector(".score-board"),result_div=document.querySelector(".result"),rock_div=document.getElementById("rock"),paper_div=document.getElementById("paper"),scissors_div=document.getElementById("scissors");function getComputerChoice(){return["rock","paper","scissors"][Math.floor(3*Math.random())]}function convertCase(e){return"paper"===e?"Paper":"scissors"===e?"Scissors":"Rock"}function win(e,s){userScore++,userScore_span.innerHTML=userScore;const o=" (user)".fontsize(3).sup(),r=" (comp)".fontsize(3).sup();result_div.innerHTML=`<p>${convertCase(e)}${o} beats ${convertCase(s)}${r}. You win!</p>`;const c=document.getElementById(e);c.classList.add("winningStyles"),setTimeout((()=>c.classList.remove("winningStyles")),300)}function loses(e,s){computerScore++,computerScore_span.innerHTML=computerScore;const o=" (user)".fontsize(3).sup(),r=" (comp)".fontsize(3).sup();result_div.innerHTML=`<p>${convertCase(s)}${r}\n    beats ${convertCase(e)}${o}. You lose!</p>`;const c=document.getElementById(e);c.classList.add("losingStyles"),setTimeout((()=>c.classList.remove("losingStyles")),300)}function draw(e,s){" (user)".fontsize(3).sup()," (comp)".fontsize(3).sup();result_div.innerHTML=`<p>It was a draw! You both chose ${convertCase(e)}</p>`;const o=document.getElementById(e);o.classList.add("drawStyles"),setTimeout((()=>o.classList.remove("drawStyles")),300)}function game(e){const s=getComputerChoice();switch(e+s){case"paperrock":case"rockscissors":case"scissorspaper":win(e,s);break;case"rockpaper":case"scissorsrock":case"paperscissors":loses(e,s);break;case"rockrock":case"scissorsscissors":case"paperpaper":draw(e,s);break}}function main(){rock_div.addEventListener("click",(()=>game("rock"))),paper_div.addEventListener("click",(()=>game("paper"))),scissors_div.addEventListener("click",(()=>game("scissors")))}main();